import React, { useEffect } from "react";
import { FETCH_CURREVENTS } from '../../actions';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import IndividualEventCard from '/../events/eventsCards/IndividualEventCard';
            
            

const LandingEventsSlider = (props) => {
    const { fetchCurrEvents, currEvents } = props

    useEffect( () => {
        fetchCurrEvents()
    }, [] );

    const renderList = () => {
        return currEvents.map( event => {
            return <IndividualEventCard 
                name={event.name}
                image={event.image}
                shortDate={event.shortDate}
                excerpt={event.excerpt}
                id={event._id}
                key={event._id}
                token={props.token}
            />
        })
    }


    if (currEvents.length > 0) {
        return (
            <div className="container" id="events-gallery-container">
                <div id="events-and-workshop-btn">
                    <Link to="/events" className="btn btn-primary my-4">Events & Workshops</Link>
                </div>
                <div className="events-gallery">
                    <div className="row">
                        {renderList()}
                    </div>
                </div>
            </div>
        )
    } else if (!currEvents) {
        <p className="h3b">
            There is no upcoming events. Please check again soon!
        </p>
    } else {
        <p className="h3b">Loading</p>
    }
}
