// import React from 'react';
import { useCallback, useEffect } from "react"
import { BrowserRouter, Route, Routes, useLocation } from 'react-router-dom';
import { connect } from 'react-redux';

// Components
import Header from './components/partials/Header';
import Landing from './components/Landing';
import Footer from './components/partials/Footer';
import EmailSubscribe from './components/partials/EmailSubscribe';

import Coaches from './components/Coaches';
import ShowEvent from './components/events/ShowEvent';
import Login from './components/admin/Login';
import ShowAllEventCards from "./components/events/eventCards/ShowAllEventCards";

import EditEvent from './components/admin/EditEvent';
import CreateEvent from './components/admin/CreateEvent';

//Actions
import { verifyUser } from './actions';

//Styling
import './stylesheets/main.css';



const App = (props) => {
  // const  { pathname  } = useLocation();
  const { verifyUser, token } = props

  const untoggleBurgerNav = (e) => {
    const burgerIcon = document.querySelector('nav input')
    console.log(e.target.tagName)
    if(e.target.tagName === "LABEL") {
      console.log("hit hamburger")
      
      if(burgerIcon.checked === false) {
        burgerIcon.checked = true
      }
      burgerIcon.checked = false
    }
    burgerIcon.checked = false
    //const burgerIcon = document.querySelector('nav input')
    //if (burgerIcon)
    //console.log(e.target.tagName)
    // burgerIcon.checked = false;
  }

  const verifyUserInApp = useCallback(() => {
    verifyUser()
  }, [verifyUser])

  useEffect( () => {
    verifyUserInApp()
  }, [verifyUserInApp])

  return (

    <div id="nav-grid-container" onClick={(e) => untoggleBurgerNav(e)}>
      {/* {heroImage} */}
      
      <BrowserRouter>
          <Header />
          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/login" element={<Login />} />
            <Route path="/events" element={<ShowAllEventCards />} />
            <Route path="/events/:id" element={<ShowEvent />} />
            <Route path="/pastevents" element={<ShowAllEventCards />} />
            <Route path="/coaches" element={<Coaches />} />
            {/* Admin Routes */}
            <Route path="/admin/createEvent" element={ !token ? <Login /> : <CreateEvent/> } />
            <Route path="/admin/editEvent/:id" element={ !token ? <Login /> : <EditEvent/> } />
          </Routes>
        </BrowserRouter>
        {/* <EmailSubscribe /> */}
        <Footer />
      
    </div>
  )
}

const mapStateToProps = state => {
  return {
      token: state.admin.token,
      pastEvents: state.events.pastEvents,
      state

  }
}

export default connect(
  mapStateToProps,
  { 
      verifyUser
  }
)(App);
