import React from 'react';
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import bigHouseLogo from '../../images/bh-pink.svg';

const Header = () => {
    const  { pathname  } = useLocation();

    const activateLi = (child) => {
        const allLis = document.querySelectorAll('nav li')
        const activeLi = document.querySelector(`nav :nth-child(${child})`);
        // const selectedLi = document.querySelector(`nav`);
        allLis.forEach( (li) => {
            if(li.className === 'active') {
                li.className = 'inactive'
            }
        })
        activeLi.className = 'active';
        // console.log(selectedLi)
        // console.log(allLis)
    }

    return (
        <header>
            <div>
                <div id="header-logos">
                    {/* <a href="https://bighouse.org.uk/"><img src={bigHouseLogo} className="big-house-logo" alt="Big House logo"></img></a> */}
                    <Link to="/"><img src="/images/RCF-logo-white.svg" className="rcfd-logo" alt="RCFD logo"></img></Link>
                    {/* <a href="/"><img src="/images/eu-only.svg" className="eu-logo" alt="Eurpean Union logo"></img></a> */}
                    {/* <a href="https://www.midlandsengine.org/"><img src="/images/me-white.svg" className="midlands-engine-logo" alt="Midlands Engine logo" ></img></a>  */}
                </div>
                <nav>
                    <Link className="inactive" to="/" ><li onClick={() => activateLi(1)}><strong>Home</strong></li></Link>
                    <Link className="inactive" to="/events" ><li onClick={() => activateLi(2)}><strong>Events & Workshops</strong></li></Link>
                    <Link className="inactive" to="/coaches"><li onClick={() => activateLi(3)}><strong>Coaches</strong></li></Link>
                    <Link className="inactive" to="/resources"><li onClick={() => activateLi(4)}><strong>Resources</strong></li></Link>
                </nav>
            </div>
        </header>
    )
}
